import React, { useState } from "react";
import styled, { ThemeProvider } from "styled-components";
import { lighten } from "polished";
import Input from "./input";
import Checkbox from "./checkbox";

const Forms = {};

let FormDiv = styled.div`
width: 25%;
margin: 0 auto;
`;

let Form = styled.form`
border: none;
span {
  float: right;
}
`;

Forms.LoginForm = props => {
    var state = { showPassword: false };

    function onPasswordChange(event) {

    }

    function handleSubmit(event) {

    }

    return (
        <FormDiv>
            <h3>Login</h3>
            <Form onSubmit={handleSubmit}>
                <Input type="text" name="username" placeholder="Username"/>
                <Input type={state.showPassword ? "text" : "password"} name="password" placeholder="Password" onchange={onPasswordChange}/>
                <Checkbox name="showPassword"/>
            </Form>
        </FormDiv>
    );
}

export default Forms;
